import React, { useState, useCallback } from 'react';
import { UploadCloud, FileText, BrainCircuit, AlertTriangle, CheckCircle, Loader, BookCopy, Server, KeyRound } from 'lucide-react';

// Main application component
function App() {
  // State for file upload
  const [selectedFile, setSelectedFile] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysisError, setAnalysisError] = useState(null);
  const [isDragOver, setIsDragOver] = useState(false);

  // State for Confluence Sync
  const [confluenceUrl, setConfluenceUrl] = useState('');
  const [confluenceUser, setConfluenceUser] = useState('');
  const [confluenceToken, setConfluenceToken] = useState('');
  const [isSyncing, setIsSyncing] = useState(false);
  const [syncResult, setSyncResult] = useState(null);
  const [syncError, setSyncError] = useState(null);


  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleAnalyze = async () => {
    if (!selectedFile) {
      setAnalysisError('Please select a file first.');
      return;
    }
    setIsAnalyzing(true);
    setAnalysisError(null);
    setAnalysisResult(null);
    const formData = new FormData();
    formData.append('log_file', selectedFile);
    try {
      const response = await fetch('http://127.0.0.1:8000/analyze/', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) {
        const errData = await response.json();
        throw new Error(errData.detail || `HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setAnalysisResult(data);
    } catch (e) {
      setAnalysisError(e.message || 'Failed to analyze the file. Ensure the backend is running.');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleSyncConfluence = async () => {
    if (!confluenceUrl || !confluenceUser || !confluenceToken) {
        setSyncError('Please fill in all Confluence fields.');
        return;
    }
    setIsSyncing(true);
    setSyncError(null);
    setSyncResult(null);
    try {
        const response = await fetch('http://127.0.0.1:8000/sync-confluence/', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                url: confluenceUrl,
                username: confluenceUser,
                api_token: confluenceToken,
            }),
        });
        if (!response.ok) {
            const errData = await response.json();
            throw new Error(errData.detail || `HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setSyncResult(data);
    } catch (e) {
        setSyncError(e.message || 'Failed to sync with Confluence.');
    } finally {
        setIsSyncing(false);
    }
  };

  // Drag and drop handlers
  const handleDragEnter = useCallback((e) => { e.preventDefault(); e.stopPropagation(); setIsDragOver(true); }, []);
  const handleDragLeave = useCallback((e) => { e.preventDefault(); e.stopPropagation(); setIsDragOver(false); }, []);
  const handleDragOver = useCallback((e) => { e.preventDefault(); e.stopPropagation(); }, []);
  const handleDrop = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragOver(false);
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      setSelectedFile(e.dataTransfer.files[0]);
      e.dataTransfer.clearData();
    }
  }, []);


  return (
    <div className="bg-slate-900 min-h-screen flex flex-col items-center justify-center font-sans text-white p-4">
      <div className="w-full max-w-6xl mx-auto">
        <header className="text-center mb-8">
          <div className="flex items-center justify-center gap-4 mb-2">
            <BrainCircuit className="h-12 w-12 text-cyan-400" />
            <h1 className="text-5xl font-bold tracking-tight">AI Incident Analyst</h1>
          </div>
          <p className="text-slate-400 text-lg">Your automated partner in incident resolution.</p>
        </header>

        {/* ==================== NEW CONFLUENCE SECTION ==================== */}
        <div className="bg-slate-800/50 rounded-2xl shadow-2xl shadow-cyan-500/10 p-8 border border-slate-700 mb-8">
            <h2 className="text-3xl font-semibold mb-6 flex items-center gap-3 border-b border-slate-700 pb-4">
                <BookCopy className="text-cyan-400" />
                Knowledge Base Sync
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 items-end">
                <InputField icon={<Server />} label="Confluence URL" placeholder="https://your-domain.atlassian.net" value={confluenceUrl} onChange={setConfluenceUrl} />
                <InputField icon={<FileText />} label="Email / Username" placeholder="your-email@example.com" value={confluenceUser} onChange={setConfluenceUser} />
                <InputField icon={<KeyRound />} label="API Token" placeholder="Your Confluence API Token" type="password" value={confluenceToken} onChange={setConfluenceToken} />
                <button
                    onClick={handleSyncConfluence}
                    disabled={isSyncing}
                    className="w-full bg-cyan-500 hover:bg-cyan-600 disabled:bg-slate-600 text-white font-bold py-3 px-4 rounded-lg flex items-center justify-center gap-2 transition-all duration-300 transform hover:scale-105"
                >
                    {isSyncing ? <><Loader className="animate-spin h-5 w-5" /> Syncing...</> : 'Sync Now'}
                </button>
            </div>
            <div className="mt-4 min-h-[50px]">
                {isSyncing && <StatusCard type="loading" message="Connecting to Confluence and fetching pages..." />}
                {syncError && <StatusCard type="error" title="Sync Failed" message={syncError} />}
                {syncResult && <SyncResultCard data={syncResult} />}
            </div>
        </div>
        {/* =============================================================== */}

        <main className="bg-slate-800/50 rounded-2xl shadow-2xl shadow-cyan-500/10 p-8 border border-slate-700">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div className="flex flex-col">
              <h2 className="text-2xl font-semibold mb-4 flex items-center gap-2"><UploadCloud className="text-cyan-400" />1. Upload Log File</h2>
              <div className={`flex-grow flex flex-col items-center justify-center border-2 border-dashed rounded-lg p-6 transition-colors duration-300 ${isDragOver ? 'border-cyan-400 bg-slate-700/50' : 'border-slate-600 hover:border-cyan-500'}`} onDragEnter={handleDragEnter} onDragLeave={handleDragLeave} onDragOver={handleDragOver} onDrop={handleDrop}>
                <input type="file" id="file-upload" className="hidden" onChange={handleFileChange} />
                <label htmlFor="file-upload" className="text-center cursor-pointer">
                    <UploadCloud className="mx-auto h-12 w-12 text-slate-400 mb-2" />
                    <span className="font-semibold text-cyan-400">Click to upload</span><span className="text-slate-400"> or drag and drop</span>
                    <p className="text-xs text-slate-500 mt-1">Syslog, Journalctl, MTR, etc.</p>
                </label>
              </div>
              {selectedFile && (
                <div className="mt-4 p-3 bg-slate-700 rounded-lg flex items-center justify-between">
                  <div className="flex items-center gap-2"><FileText className="h-5 w-5 text-slate-400" /><span className="text-sm font-mono">{selectedFile.name}</span></div>
                  <button onClick={() => setSelectedFile(null)} className="text-slate-400 hover:text-white">&times;</button>
                </div>
              )}
              <button onClick={handleAnalyze} disabled={!selectedFile || isAnalyzing} className="mt-6 w-full bg-cyan-500 hover:bg-cyan-600 disabled:bg-slate-600 disabled:cursor-not-allowed text-white font-bold py-3 px-4 rounded-lg flex items-center justify-center gap-2 transition-all duration-300 transform hover:scale-105">
                {isAnalyzing ? <><Loader className="animate-spin h-5 w-5" /> Analyzing...</> : <><BrainCircuit className="h-5 w-5" /> Analyze Now</>}
              </button>
            </div>
            <div className="flex flex-col">
              <h2 className="text-2xl font-semibold mb-4">2. Get Analysis</h2>
              <div className="bg-slate-900/70 rounded-lg p-6 flex-grow border border-slate-700">
                {analysisError && <StatusCard type="error" title="Analysis Failed" message={analysisError} />}
                {isAnalyzing && <StatusCard type="loading" message="Processing file and consulting AI knowledge base..." />}
                {analysisResult && <AnalysisResultCard data={analysisResult} />}
                {!isAnalyzing && !analysisResult && !analysisError && (<div className="text-center text-slate-500 h-full flex flex-col justify-center items-center"><BrainCircuit size={48} className="mb-4 opacity-30"/><p>Analysis results will appear here.</p></div>)}
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}

const InputField = ({ icon, label, placeholder, type = "text", value, onChange }) => (
    <div>
        <label className="text-sm font-medium text-slate-300 flex items-center gap-1.5 mb-1.5">{icon}{label}</label>
        <input
            type={type}
            placeholder={placeholder}
            value={value}
            onChange={(e) => onChange(e.target.value)}
            className="w-full bg-slate-700/50 border border-slate-600 rounded-md py-2 px-3 text-white placeholder-slate-400 focus:ring-2 focus:ring-cyan-500 focus:border-cyan-500 transition"
        />
    </div>
);

const StatusCard = ({ type, title, message }) => {
  if (type === 'error') {
    return (
      <div className="bg-red-500/10 border border-red-500/30 text-red-300 p-4 rounded-lg flex items-center gap-3">
        <AlertTriangle />
        <div><p className="font-semibold">{title}</p><p className="text-sm">{message}</p></div>
      </div>
    );
  }
  if (type === 'loading') {
    return (
      <div className="text-center text-slate-400 h-full flex flex-col justify-center items-center animate-pulse">
        <Loader size={40} className="mb-3 opacity-50 animate-spin"/><p>{message}</p>
      </div>
    );
  }
  return null;
};

const AnalysisResultCard = ({ data }) => (
    <div className="bg-green-500/10 border border-green-500/30 text-green-300 p-4 rounded-lg flex items-start gap-3">
        <CheckCircle className="flex-shrink-0 mt-1"/>
        <div>
            <p className="font-semibold mb-2">Analysis Complete!</p>
            <div className="text-sm text-slate-300 bg-slate-800 p-3 rounded-md">
                <p><strong className="text-slate-100">File Received:</strong> {data.filename}</p>
                <p><strong className="text-slate-100">File Size:</strong> {data.size_kb.toFixed(2)} KB</p>
                <p className="mt-2 italic text-slate-400">{data.message}</p>
            </div>
        </div>
    </div>
);

const SyncResultCard = ({ data }) => (
     <div className="bg-green-500/10 border border-green-500/30 text-green-300 p-4 rounded-lg">
        <div className="flex items-start gap-3">
            <CheckCircle className="flex-shrink-0 mt-1"/>
            <div>
                <p className="font-semibold">Sync Successful</p>
                <p className="text-sm">{data.message}</p>
            </div>
        </div>
        {data.details && (
            <div className="mt-3 pt-3 border-t border-green-500/20 text-xs text-slate-400 space-y-1">
                {Object.entries(data.details).map(([label, info]) => (
                    <div key={label} className="flex justify-between items-center">
                        <span className="font-mono bg-slate-700/50 px-1.5 py-0.5 rounded">{label}</span>
                        <span className="font-semibold">{info.count} pages found</span>
                    </div>
                ))}
            </div>
        )}
    </div>
);

export default App;
