// frontend/src/App.js
// This is the updated main component for your React application.

import React, { useState, useEffect, useCallback } from 'react';
import { Upload, FileText, Settings, RefreshCw } from 'lucide-react';

// --- Helper Components ---

// A simple status indicator dot
const StatusDot = ({ color }) => (
  <span className={`w-3 h-3 rounded-full inline-block mr-2 ${color}`}></span>
);

// A component for the status bar at the top
const StatusBar = ({ backendStatus, confluenceStatus, lastSync, onSync, isSyncing }) => (
  <div className="bg-slate-100 p-3 rounded-lg mb-6 text-sm text-slate-600 flex justify-between items-center shadow-sm">
    <div className="flex items-center">
      <span className="font-semibold mr-6 flex items-center">
        {backendStatus === 'Online' ? <StatusDot color="bg-green-500" /> : <StatusDot color="bg-red-500" />}
        Backend: {backendStatus}
      </span>
      <span className="font-semibold mr-6 flex items-center">
        {confluenceStatus === 'Connected' && <StatusDot color="bg-green-500" />}
        {confluenceStatus === 'Syncing...' && <StatusDot color="bg-yellow-500" />}
        {confluenceStatus.includes('Error') && <StatusDot color="bg-red-500" />}
        {confluenceStatus === 'Not Synced' && <StatusDot color="bg-slate-400" />}
        Confluence: {confluenceStatus}
      </span>
    </div>
    <div className="flex items-center">
      <span className="mr-4">Last sync: {lastSync}</span>
      <button
        onClick={onSync}
        disabled={isSyncing || backendStatus !== 'Online'}
        className="flex items-center px-3 py-1.5 bg-indigo-500 text-white rounded-md hover:bg-indigo-600 disabled:bg-indigo-300 disabled:cursor-not-allowed transition-all duration-200"
      >
        <RefreshCw className={`w-4 h-4 mr-2 ${isSyncing ? 'animate-spin' : ''}`} />
        {isSyncing ? 'Syncing...' : 'Sync Now'}
      </button>
    </div>
  </div>
);


// --- Main App Component ---

function App() {
  // --- State Management ---
  const [incidentTitle, setIncidentTitle] = useState('');
  const [description, setDescription] = useState('');
  const [severity, setSeverity] = useState('');
  const [tags, setTags] = useState('');
  const [file, setFile] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  // New state for backend and Confluence status
  const [backendStatus, setBackendStatus] = useState('Checking...');
  const [confluenceStatus, setConfluenceStatus] = useState('Not Synced');
  const [lastSync, setLastSync] = useState('Never');
  const [isSyncing, setIsSyncing] = useState(false);

  const API_URL = "http://localhost:8000";

  // --- Effects ---

  // Check backend status on initial load
  useEffect(() => {
    fetch(API_URL)
      .then(response => {
        if (response.ok) {
          setBackendStatus('Online');
        } else {
          setBackendStatus('Offline');
        }
      })
      .catch(() => {
        setBackendStatus('Offline');
      });
  }, []);

  // --- Handlers ---

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleDragEnter = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      setFile(e.dataTransfer.files[0]);
      e.dataTransfer.clearData();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file && !incidentTitle) {
      alert('Please provide an incident title or upload a log file.');
      return;
    }
    setIsLoading(true);
    setAnalysisResult(null);

    const formData = new FormData();
    if (file) {
      formData.append('log_file', file);
    }
    // You can append other form data as well if your backend handles it
    // formData.append('title', incidentTitle);
    // formData.append('description', description);

    try {
      const response = await fetch(`${API_URL}/analyze-incident/`, {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setAnalysisResult({ type: 'success', data });
    } catch (error) {
      setAnalysisResult({ type: 'error', message: error.toString() });
    } finally {
      setIsLoading(false);
    }
  };

  // New handler to trigger Confluence sync
  const handleSyncConfluence = useCallback(async () => {
    setIsSyncing(true);
    setConfluenceStatus('Syncing...');
    try {
      const response = await fetch(`${API_URL}/sync-confluence/`);
      const data = await response.json();

      if (!response.ok) {
        // Use detail from FastAPI's HTTPException if available
        const errorDetail = data.detail || 'Failed to sync';
        throw new Error(errorDetail);
      }

      setConfluenceStatus('Connected');
      setLastSync(new Date().toLocaleString());
      // You could show a success message here, e.g., using a toast notification
      alert(`Sync successful! Found ${data.pages_found} pages.`);

    } catch (error) {
      setConfluenceStatus(`Error: ${error.message}`);
    } finally {
      setIsSyncing(false);
    }
  }, []);


  return (
    <div className="bg-slate-50 min-h-screen font-sans">
      <div className="container mx-auto p-4 md:p-8">

        <header className="text-center mb-8">
          <h1 className="text-4xl md:text-5xl font-bold text-indigo-700 mb-2">
            AI Incident Analyst
          </h1>
          <p className="text-lg text-slate-500">
            Analyze incidents using historical data and AI-powered insights
          </p>
        </header>

        {/* --- Status Bar --- */}
        <StatusBar 
          backendStatus={backendStatus}
          confluenceStatus={confluenceStatus}
          lastSync={lastSync}
          onSync={handleSyncConfluence}
          isSyncing={isSyncing}
        />

        <main className="grid grid-cols-1 lg:grid-cols-2 gap-6">

          {/* --- Incident Reporting Form --- */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-semibold text-slate-800 mb-4 flex items-center">
              <FileText className="mr-3 text-indigo-500" />
              Report New Incident
            </h2>
            <form onSubmit={handleSubmit}>
              <div className="space-y-4">
                <div>
                  <label htmlFor="incidentTitle" className="block text-sm font-medium text-slate-700 mb-1">Incident Title</label>
                  <input type="text" id="incidentTitle" value={incidentTitle} onChange={e => setIncidentTitle(e.target.value)} className="w-full px-3 py-2 border border-slate-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" placeholder="Brief description of the incident" />
                </div>
                <div>
                  <label htmlFor="description" className="block text-sm font-medium text-slate-700 mb-1">Description</label>
                  <textarea id="description" value={description} onChange={e => setDescription(e.target.value)} rows="4" className="w-full px-3 py-2 border border-slate-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" placeholder="Detailed description of what happened..."></textarea>
                </div>
                <div>
                  <label htmlFor="severity" className="block text-sm font-medium text-slate-700 mb-1">Severity</label>
                  <select id="severity" value={severity} onChange={e => setSeverity(e.target.value)} className="w-full px-3 py-2 border border-slate-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500">
                    <option value="">Select severity</option>
                    <option value="1">SEV 1 (Critical)</option>
                    <option value="2">SEV 2 (High)</option>
                    <option value="3">SEV 3 (Medium)</option>
                    <option value="4">SEV 4 (Low)</option>
                  </select>
                </div>
                <div>
                  <label htmlFor="tags" className="block text-sm font-medium text-slate-700 mb-1">Tags (comma-separated)</label>
                  <input type="text" id="tags" value={tags} onChange={e => setTags(e.target.value)} className="w-full px-3 py-2 border border-slate-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" placeholder="database, timeout, production" />
                </div>
              </div>
            </form>
          </div>

          {/* --- File Upload & Analysis Button --- */}
          <div className="space-y-6">
            <div className="bg-white p-6 rounded-lg shadow-md">
              <h2 className="text-2xl font-semibold text-slate-800 mb-4 flex items-center">
                <Upload className="mr-3 text-indigo-500" />
                Upload Log Files
              </h2>
              <div
                onDragEnter={handleDragEnter}
                onDragOver={handleDragEnter}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
                className={`border-2 border-dashed rounded-lg p-10 text-center cursor-pointer transition-colors duration-200 ${isDragging ? 'border-indigo-500 bg-indigo-50' : 'border-slate-300 hover:border-indigo-400'}`}
              >
                <input type="file" onChange={handleFileChange} className="hidden" id="file-upload" />
                <label htmlFor="file-upload" className="cursor-pointer">
                  <FileText className="mx-auto h-12 w-12 text-slate-400" />
                  <p className="mt-2 text-slate-600">
                    {file ? `Selected: ${file.name}` : 'Drag & drop log files here or click to browse'}
                  </p>
                  <p className="text-xs text-slate-500 mt-1">Supports: .log, .txt, syslog, journalctl output</p>
                </label>
              </div>
            </div>

            <button
              onClick={handleSubmit}
              disabled={isLoading}
              className="w-full flex justify-center items-center py-3 px-4 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:bg-indigo-400"
            >
              {isLoading ? (
                <>
                  <Settings className="animate-spin mr-2" />
                  Analyzing...
                </>
              ) : 'Analyze Incident'}
            </button>
          </div>

        </main>
      </div>
    </div>
  );
}

