import React, { useState, useEffect, useCallback } from 'react';
import { Upload, FileText, Settings, RefreshCw, Search, Link as LinkIcon, AlertCircle, Tag, MessageSquare, AlertTriangle as CauseIcon, CheckSquare } from 'lucide-react';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

// --- Helper Components ---

const StatusDot = ({ color }) => (
  <span className={`w-3 h-3 rounded-full inline-block mr-2 ${color}`}></span>
);

const StatusBar = ({ backendStatus, onSync, isSyncing, totalDocs }) => (
  <div className="bg-slate-100 p-3 rounded-lg mb-6 text-sm text-slate-600 flex justify-between items-center shadow-sm">
    <div className="flex items-center">
      <span className="font-semibold mr-6 flex items-center">
        {backendStatus === 'Online' ? <StatusDot color="bg-green-500" /> : <StatusDot color="bg-red-500" />}
        Backend: {backendStatus}
      </span>
      <span className="font-semibold mr-6 flex items-center">
        {totalDocs > 0 ? <StatusDot color="bg-green-500" /> : <StatusDot color="bg-yellow-500" />}
        Knowledge Base: {totalDocs > 0 ? `${totalDocs} documents indexed` : 'Not Synced'}
      </span>
    </div>
    <div className="flex items-center">
      <button
        onClick={onSync}
        disabled={isSyncing || backendStatus !== 'Online'}
        className="flex items-center px-3 py-1.5 bg-indigo-500 text-white rounded-md hover:bg-indigo-600 disabled:bg-indigo-300 disabled:cursor-not-allowed transition-all duration-200"
      >
        <RefreshCw className={`w-4 h-4 mr-2 ${isSyncing ? 'animate-spin' : ''}`} />
        {isSyncing ? 'Syncing...' : 'Sync Data'}
      </button>
    </div>
  </div>
);

const CategoryBadge = ({ category }) => {
    const categoryColors = {
        Database: 'bg-blue-100 text-blue-800',
        Network: 'bg-green-100 text-green-800',
        Application: 'bg-purple-100 text-purple-800',
        Authentication: 'bg-yellow-100 text-yellow-800',
        Infrastructure: 'bg-red-100 text-red-800',
        General: 'bg-slate-100 text-slate-800',
    };
    const color = categoryColors[category] || categoryColors.General;
    return (
        <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${color}`}>
            <Tag className="w-4 h-4 mr-2" />
            {category}
        </span>
    );
};


const SearchResults = ({ results, isLoading }) => {
  if (isLoading) {
    return (
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-slate-800 mb-4 flex items-center">
          <Search className="mr-3 text-indigo-500" />
          Analyzing...
        </h2>
        <div className="flex justify-center items-center p-8">
          <Settings className="w-12 h-12 text-indigo-500 animate-spin" />
        </div>
      </div>
    );
  }

  if (!results) return null;

  if (results.type === 'error') {
    return (
       <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold text-red-600 mb-4 flex items-center">
          <AlertCircle className="mr-3" />
          Analysis Failed
        </h2>
        <p className="text-slate-600 bg-red-50 p-4 rounded-md">{results.message}</p>
      </div>
    )
  }

  const { search, analysis, runbook } = results.data;

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-semibold text-slate-800 mb-4 flex items-center">
        <Search className="mr-3 text-indigo-500" />
        Analysis Results
      </h2>
      <div className="space-y-6">
        {analysis && (
          <div className="p-4 bg-slate-50 rounded-lg border border-slate-200">
            <h3 className="font-semibold text-lg mb-3">AI Analysis</h3>
            <div className="space-y-3">
                <div className="flex items-center justify-between">
                    <span className="font-medium text-slate-600">Probable Category:</span>
                    <CategoryBadge category={analysis.category} />
                </div>
                <div>
                    <h4 className="font-medium text-slate-600 mb-1 flex items-center"><MessageSquare className="w-4 h-4 mr-2" />Summary</h4>
                    <p className="text-slate-700 text-sm">{analysis.summary}</p>
                </div>
                 <div>
                    <h4 className="font-medium text-slate-600 mb-1 flex items-center"><CauseIcon className="w-4 h-4 mr-2" />Probable Cause</h4>
                    <p className="text-slate-700 text-sm">{analysis.probable_cause}</p>
                </div>
            </div>
          </div>
        )}

        {runbook && (
            <div className="p-4 bg-indigo-50 rounded-lg border border-indigo-200">
                <h3 className="font-semibold text-lg mb-3 text-indigo-800 flex items-center">
                    <CheckSquare className="w-5 h-5 mr-2" />
                    Generated Runbook (Immediate Actions)
                </h3>
                <div className="prose prose-sm max-w-none text-slate-700">
                    <ReactMarkdown remarkPlugins={[remarkGfm]}>
                        {runbook.runbook_markdown}
                    </ReactMarkdown>
                </div>
            </div>
        )}

        <div>
            <h3 className="font-semibold text-lg border-t pt-4">Similar Historical Incidents:</h3>
            {search?.results?.length > 0 ? (
              <ul className="divide-y divide-slate-200">
                {search.results.map((item, index) => (
                  <li key={index} className="py-3">
                    <p className="font-medium text-slate-800">{item.document.title}</p>
                    <p className="text-sm text-slate-500">
                      Similarity Score: {item.similarity_score.toFixed(4)}
                    </p>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-slate-500">No similar incidents found in the knowledge base.</p>
            )}
        </div>
      </div>
    </div>
  );
};


// --- Main App Component ---

function App() {
  const [incidentTitle, setIncidentTitle] = useState('');
  const [description, setDescription] = useState('');
  const [file, setFile] = useState(null);

  const [analysisResult, setAnalysisResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const [backendStatus, setBackendStatus] = useState('Checking...');
  const [isSyncing, setIsSyncing] = useState(false);
  const [totalDocs, setTotalDocs] = useState(0);

  const API_URL = "http://localhost:8000";

  const handleSyncData = useCallback(async () => {
    setIsSyncing(true);
    try {
      const response = await fetch(`${API_URL}/sync-local-data/`);
      const data = await response.json();
      if (!response.ok) throw new Error(data.detail || 'Failed to sync');
      setTotalDocs(data.total_pages_in_store || 0);
      alert(`Sync successful! Indexed ${data.pages_added} documents.`);
    } catch (error) {
      alert(`Sync Error: ${error.message}`);
    } finally {
      setIsSyncing(false);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    let query = description || incidentTitle;

    if (file) {
      try {
        query = await file.text();
      } catch (readError) {
        setAnalysisResult({ type: 'error', message: 'Could not read the uploaded file.' });
        return;
      }
    }

    if (!query) {
      alert('Please provide an incident title, description, or a log file for analysis.');
      return;
    }

    setIsLoading(true);
    setAnalysisResult(null);

    try {
      // Perform all three API calls concurrently for a faster user experience
      const [searchResponse, analysisResponse, runbookResponse] = await Promise.all([
        fetch(`${API_URL}/search-incidents/`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ query: query }),
        }),
        fetch(`${API_URL}/analyze-incident-details/`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ query: query }),
        }),
        fetch(`${API_URL}/generate-runbook/`, { // <-- NEW API CALL
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ query: query }),
        })
      ]);

      if (!searchResponse.ok || !analysisResponse.ok || !runbookResponse.ok) {
        throw new Error(`An API error occurred.`);
      }

      const searchData = await searchResponse.json();
      const analysisData = await analysisResponse.json();
      const runbookData = await runbookResponse.json();

      setAnalysisResult({ type: 'success', data: { search: searchData, analysis: analysisData.analysis, runbook: runbookData } });

    } catch (error) {
      setAnalysisResult({ type: 'error', message: error.toString() });
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetch(API_URL)
      .then(response => response.ok ? setBackendStatus('Online') : setBackendStatus('Offline'))
      .catch(() => setBackendStatus('Offline'));
  }, []);


  return (
    <div className="bg-slate-50 min-h-screen font-sans">
      <div className="container mx-auto p-4 md:p-8">

        <header className="text-center mb-8">
          <h1 className="text-4xl md:text-5xl font-bold text-indigo-700 mb-2">
            AIOps Co-pilot
          </h1>
          <p className="text-lg text-slate-500">
            Analyze incidents using historical data and AI-powered insights
          </p>
        </header>

        <StatusBar 
          backendStatus={backendStatus}
          onSync={handleSyncData}
          isSyncing={isSyncing}
          totalDocs={totalDocs}
        />

        <main className="grid grid-cols-1 lg:grid-cols-2 gap-6">

          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-semibold text-slate-800 mb-4 flex items-center">
              <FileText className="mr-3 text-indigo-500" />
              Report & Analyze New Incident
            </h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label htmlFor="incidentTitle" className="block text-sm font-medium text-slate-700 mb-1">Incident Title</label>
                  <input type="text" id="incidentTitle" value={incidentTitle} onChange={e => setIncidentTitle(e.target.value)} className="w-full px-3 py-2 border border-slate-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" placeholder="e.g., API Gateway 502 Errors" />
                </div>
                <div>
                  <label htmlFor="description" className="block text-sm font-medium text-slate-700 mb-1">Description / Logs</label>
                  <textarea id="description" value={description} onChange={e => setDescription(e.target.value)} rows="6" className="w-full px-3 py-2 border border-slate-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500" placeholder="Describe the issue or paste relevant log snippets..."></textarea>
                </div>
                 <button
                    type="submit"
                    disabled={isLoading || backendStatus !== 'Online'}
                    className="w-full flex justify-center items-center py-3 px-4 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:bg-indigo-400 disabled:cursor-not-allowed"
                  >
                    {isLoading ? (
                      <>
                        <Settings className="animate-spin mr-2" />
                        Analyzing...
                      </>
                    ) : 'Analyze Incident'}
                  </button>
            </form>
          </div>

          <div className="lg:col-span-1">
            <SearchResults results={analysisResult} isLoading={isLoading} />
          </div>

        </main>
      </div>
    </div>
  );
}

export default App;
